import{_ as a,c as o,o as i,ai as l}from"./chunks/framework.Ycja6nMq.js";const p=JSON.parse('{"title":"Offline Reading","description":"","frontmatter":{},"headers":[],"relativePath":"guides/offline-download.md","filePath":"guides/offline-download.md"}'),t={name:"guides/offline-download.md"};function n(r,e,d,s,u,c){return i(),o("div",null,e[0]||(e[0]=[l('<h1 id="offline-reading" tabindex="-1">Offline Reading <a class="header-anchor" href="#offline-reading" aria-label="Permalink to &quot;Offline Reading&quot;">​</a></h1><p>Comicers provides powerful offline reading capabilities, allowing you to download and read manga without an internet connection.</p><h2 id="downloading-chapters" tabindex="-1">Downloading Chapters <a class="header-anchor" href="#downloading-chapters" aria-label="Permalink to &quot;Downloading Chapters&quot;">​</a></h2><h3 id="basic-download" tabindex="-1">Basic Download <a class="header-anchor" href="#basic-download" aria-label="Permalink to &quot;Basic Download&quot;">​</a></h3><ol><li>Open any series in your library</li><li>Click <code>Options</code> → <code>Download</code> in the top right</li><li>Select chapters to download: <ul><li>Individual chapters</li><li>Multiple chapters</li><li>Entire series</li></ul></li><li>Click &quot;Download&quot; to start</li></ol><h3 id="advanced-download-options" tabindex="-1">Advanced Download Options <a class="header-anchor" href="#advanced-download-options" aria-label="Permalink to &quot;Advanced Download Options&quot;">​</a></h3><p>In <code>Settings</code> → <code>Downloads</code>, you can configure:</p><ul><li><strong>Download Location</strong>: Choose where to save downloaded chapters</li><li><strong>Image Quality</strong>: Select between: <ul><li>Original quality</li><li>Optimized (smaller file size)</li><li>Custom quality settings</li></ul></li><li><strong>Download Queue</strong>: Set maximum concurrent downloads</li><li><strong>Auto-download</strong>: Configure automatic downloading of: <ul><li>New chapters</li><li>Next chapters in series</li><li>Updates to existing chapters</li></ul></li></ul><h2 id="managing-downloads" tabindex="-1">Managing Downloads <a class="header-anchor" href="#managing-downloads" aria-label="Permalink to &quot;Managing Downloads&quot;">​</a></h2><h3 id="download-queue" tabindex="-1">Download Queue <a class="header-anchor" href="#download-queue" aria-label="Permalink to &quot;Download Queue&quot;">​</a></h3><p>Access the download queue from the <code>Downloads</code> tab to:</p><ul><li>View download progress</li><li>Pause/resume downloads</li><li>Cancel downloads</li><li>Clear the queue</li><li>Set download priorities</li></ul><h3 id="downloaded-content" tabindex="-1">Downloaded Content <a class="header-anchor" href="#downloaded-content" aria-label="Permalink to &quot;Downloaded Content&quot;">​</a></h3><p>Manage your downloaded chapters:</p><ol><li>Go to the <code>Downloads</code> page</li><li>View all downloaded content</li><li>Options available: <ul><li>Delete individual chapters</li><li>Clear all downloads</li><li>Export downloaded content</li><li>Check storage usage</li></ul></li></ol><h2 id="offline-reading-features" tabindex="-1">Offline Reading Features <a class="header-anchor" href="#offline-reading-features" aria-label="Permalink to &quot;Offline Reading Features&quot;">​</a></h2><h3 id="automatic-offline-mode" tabindex="-1">Automatic Offline Mode <a class="header-anchor" href="#automatic-offline-mode" aria-label="Permalink to &quot;Automatic Offline Mode&quot;">​</a></h3><p>Comicers automatically:</p><ul><li>Uses downloaded versions when offline</li><li>Syncs progress when back online</li><li>Manages storage efficiently</li><li>Handles updates seamlessly</li></ul><h3 id="storage-management" tabindex="-1">Storage Management <a class="header-anchor" href="#storage-management" aria-label="Permalink to &quot;Storage Management&quot;">​</a></h3><p>Configure storage settings:</p><ul><li>Set maximum storage limit</li><li>Choose cleanup options</li><li>Enable automatic cleanup</li><li>Monitor storage usage</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="download-management" tabindex="-1">Download Management <a class="header-anchor" href="#download-management" aria-label="Permalink to &quot;Download Management&quot;">​</a></h3><ul><li>Download chapters before traveling</li><li>Use quality settings appropriate for your device</li><li>Regularly clean up unused downloads</li><li>Monitor storage space</li></ul><h3 id="performance-tips" tabindex="-1">Performance Tips <a class="header-anchor" href="#performance-tips" aria-label="Permalink to &quot;Performance Tips&quot;">​</a></h3><ul><li>Download during off-peak hours</li><li>Use a stable internet connection</li><li>Keep enough free storage space</li><li>Update downloaded content regularly</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="common-issues" tabindex="-1">Common Issues <a class="header-anchor" href="#common-issues" aria-label="Permalink to &quot;Common Issues&quot;">​</a></h3><ol><li><p><strong>Download Failed</strong></p><ul><li>Check internet connection</li><li>Verify storage space</li><li>Try reducing quality settings</li><li>Restart the download</li></ul></li><li><p><strong>Storage Issues</strong></p><ul><li>Clear unused downloads</li><li>Adjust quality settings</li><li>Move downloads to external storage</li><li>Update storage settings</li></ul></li><li><p><strong>Sync Problems</strong></p><ul><li>Check internet connection</li><li>Verify download status</li><li>Clear download cache</li><li>Restart the application</li></ul></li></ol><h2 id="advanced-features" tabindex="-1">Advanced Features <a class="header-anchor" href="#advanced-features" aria-label="Permalink to &quot;Advanced Features&quot;">​</a></h2><h3 id="batch-operations" tabindex="-1">Batch Operations <a class="header-anchor" href="#batch-operations" aria-label="Permalink to &quot;Batch Operations&quot;">​</a></h3><ul><li>Download multiple series</li><li>Schedule downloads</li><li>Set download priorities</li><li>Export download lists</li></ul><h3 id="storage-optimization" tabindex="-1">Storage Optimization <a class="header-anchor" href="#storage-optimization" aria-label="Permalink to &quot;Storage Optimization&quot;">​</a></h3><ul><li>Automatic cleanup</li><li>Quality optimization</li><li>Storage monitoring</li><li>Backup options</li></ul><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">​</a></h2><p>Need help with offline reading? Check out:</p><ul><li><a href="./getting-started.html">Getting Started Guide</a></li><li><a href="./customize.html">Customization Guide</a></li><li><a href="https://discord.gg/comicers" target="_blank" rel="noreferrer">Discord Community</a></li><li><a href="https://github.com/TheFizFactor/Comicers-App/issues" target="_blank" rel="noreferrer">GitHub Issues</a></li></ul>',38)]))}const g=a(t,[["render",n]]);export{p as __pageData,g as default};
